parameters.Schema:
schema({
    Name:string().required('Must enter name'),
    Character:string().required('Must select character'),
    Choice:string().required()
})

parameters.functionScript:
if(!Inputs.Enabled)
    Outputs.State = "Disabled"
else if(Inputs.Pressed)
    Outputs.State = "Pressed"
else if(Inputs.Hover)
    Outputs.State = "Hover"
else
    Outputs.State = "Default"
